mapper.select:
select

 from topLesson
 where book_id = #{book_id,jdbcType=VARCHAR}
   and logyear = #{logyear,jdbcType=VARCHAR}

mapper.delete:
delete from topLesson
where book_id = #{book_id,jdbcType=VARCHAR}
  and logyear = #{logyear,jdbcType=VARCHAR}

mapper.insert:
insert into topLesson (book_id, logyear, book_num
  )
values (#{book_id,jdbcType=VARCHAR}, #{logyear,jdbcType=VARCHAR}, #{book_num,jdbcType=INTEGER}
  )

mapper.update:
update topLesson


    book_num = #{book_num,jdbcType=INTEGER},


where book_id = #{book_id,jdbcType=VARCHAR}
  and logyear = #{logyear,jdbcType=VARCHAR}

update topLesson
set book_num = #{book_num,jdbcType=INTEGER}
where book_id = #{book_id,jdbcType=VARCHAR}
  and logyear = #{logyear,jdbcType=VARCHAR}
